import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner'
import PropTypes from 'prop-types'
import newsImg from './news.jpg'

export default class News extends Component {

    static defaultProps={
        coutry:'in',
        category:'general',
        api_key:'988181bedd4b4afd92b8f02d3b7e536b',
        pageSize:6,       
    }

    static propsTypes={
        country: PropTypes.string,
        category: PropTypes.string,
        api_key: PropTypes.string,
        pageSize:PropTypes.number,
    }

    constructor() {
        super()
        this.state = {
            articles: [],
            loading: false,
            page: 1,
        }
    }

    updateNews = async () => {
        let URL = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.props.api_key}&page=${this.state.page}&pagesize=${this.props.pageSize}`
        let data = await fetch(URL)
        let parseData = await data.json()
        this.setState({
            loading:true
        })
        console.log(parseData)
        console.log(this.state.page)
        this.setState({ 
            articles: parseData.articles,
            totalResults: parseData.totalResults,
            loading:false })
    }
    async componentDidMount() {
       // let URL = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.props.api_key}&page=${this.state.page}&pagesize=${this.props.pageSize}`
       // let data = await fetch(URL)
       // let parseData = await data.json()
       // this.setState({
       //     loading:true
       // })
       // console.log(parseData)
       // console.log(this.state.page)
       // this.setState({ 
       //     articles: parseData.articles,
       //     totalResults: parseData.totalResults,
       //     loading:false })
       this.updateNews()
    }

    handleNextClick = async () => {

        if (!(this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)))  {
            let URL = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.props.api_key}&page=${this.state.page + 1}&pagesize=${this.props.pageSize}`
            this.setState({
                loading:true
            })
            let data = await fetch(URL)
            let parseData = await data.json()
            this.setState({
                articles: parseData.articles,
                page: this.state.page + 1,
                loading: false
            })
            console.log(this.state.page)
            console.log(parseData)
        }
    }

    handlePrevClick = async () => {
        let URL = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.props.api_key}&page=${this.state.page - 1}&pagesize=${this.props.pageSize}`
        this.setState({
            loading:true
        })
        let data = await fetch(URL)
        let parseData = await data.json()
        this.setState({
            articles: parseData.articles,
            page: this.state.page - 1,
            loading: false
        })
        console.log(parseData)
        console.log(this.state.page)
    }

    render() {
        return (
            <div className='container my-3'>
                <h1 className='text-center' style={{margin:'35px 0px'}} >NewsPanda - Top Headlines</h1>
                { this.state.loading && <Spinner />}
                <div className='row my-4'>
                    { !this.state.loading && this.state.articles.map((element) => {
                        return <div className='col-md-4 my-2' key={element.url}>
                            <NewsItem title={element.title.slice(0, 45)} description={element.description ? element.description.slice(0, 88) : " "} imgURL={element.urlToImage ? element.urlToImage: newsImg} newsURL={element.url} author={element.author} publishedAt={element.publishedAt} source={element.source.name} />
                        </div>
                    })}
                </div>
                <div className='container my-2 d-flex justify-content-between'>
                    <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handlePrevClick} >&#x2B05; Previous </button>
                    <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults)/this.props.pageSize } type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &#x27A1;</button>
                </div>
            </div>
        )
    }
}
