import React, { Component } from 'react'

export default class NewsItem extends Component {
  render() {
    let { title, description, imgURL, newsURL, author, publishedAt, source } = this.props;
    return (
      <div className='my-3'>
      <div className="card">
        <img src={imgURL} className=" card-img-top" alt="..." />
        <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger" style={{left:'90%', zIndex:'1'}}>
          {source}
        </span>

        <div className="card-body">
          <h5 className="card-title">{title}...</h5>
          <p className="card-text">{description}...</p>
          <p className="card-text"><small> By {author ? author : 'Unknown'} on {new Date(publishedAt).toGMTString()} ago</small></p>
          <a href={newsURL} rel="noreferrer" target="_blank" className="btn btn-sm btn-dark">Read more</a>
        </div>
      </div></div>
    )
  }
}
