import React, { Component } from 'react'
import unkonwImg from './news.jpg'
import { Link } from 'react-router-dom'
import NewsDetails from './NewsDetails'

export default class NewsItem extends Component {
  handleReadmore = (e,title) =>{
      console.log(title);
      <NewsDetails data={title} />      
  }
   render() {
    let { title, description, imgURL, newsURL, author, publishedAt, source } = this.props;
    return (
      <div className='my-3'>
        <div className="card">
          <div style={{
            display: 'flex',
            justifyContent: 'flex-end',
            position: 'absolute',
            right: '0'
          }} >

            <span className="badge rounded-pill bg-danger" > {source} </span>
          </div>
          <img src={imgURL ? imgURL : unkonwImg} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{title}...</h5>
            <p className="card-text">{description}...</p>
            <p className="card-text"><small className='text-muted'> By {author ? author : 'Unknown'} on {new Date(publishedAt).toGMTString()} ago</small></p>
            {/*<a href={newsURL} rel="noreferrer" target="_blank" className="btn btn-sm btn-dark">Read more</a> */}
            <Link className="btn btn-sm btn-dark" to="/readmore" onClick={(e) => this.handleReadmore(e, title)}> Read more </Link>
          </div>
        </div>
      </div>
    )
  }
}
