import './App.css'
import React, { Component } from 'react'
import NavBar from './components/NavBar'
import News from './components/News'
import {
  HashRouter as Router,
  Routes,
  Route
} from "react-router-dom"
import LoadingBar from 'react-top-loading-bar'

export default class App extends Component {
  pageSize = 15
  state = { progress: 0 }

  setProgress = (progress) => {
    this.setState({ progress: progress })
  }

  render() {
    return (
      <Router>
        <div className="App">

          <NavBar />

          <LoadingBar color='#f11946' height={4} progress={this.state.progress} />
          <Routes>
            <Route path="/" exact element={<News key="general" setProgress={this.setProgress} pageSize={this.pageSize} country="in" category="general" api_key="988181bedd4b4afd92b8f02d3b7e536b" />} />
            <Route path="/business" exact element={<News key="business" setProgress={this.setProgress} pageSize={this.pageSize} country="in" category="business" api_key="988181bedd4b4afd92b8f02d3b7e536b" />} />
            <Route path="/entertainment" exact element={<News key="entertainment" setProgress={this.setProgress} pageSize={this.pageSize} country="in" category="entertainment" api_key="988181bedd4b4afd92b8f02d3b7e536b" />} />
            <Route path="/health" exact element={<News key="health" setProgress={this.setProgress} pageSize={this.pageSize} country="in" category="health" api_key="988181bedd4b4afd92b8f02d3b7e536b" />} />
            <Route path="/science" exact element={<News key="science" setProgress={this.setProgress} pageSize={this.pageSize} country="in" category="science" api_key="988181bedd4b4afd92b8f02d3b7e536b" />} />
            <Route path="/sports" exact element={<News key="sports" setProgress={this.setProgress} pageSize={this.pageSize} country="in" category="sports" api_key="988181bedd4b4afd92b8f02d3b7e536b" />} />
            <Route path="/technology" exact element={<News key="technology" setProgress={this.setProgress} pageSize={this.pageSize} country="in" category="technology" api_key="988181bedd4b4afd92b8f02d3b7e536b" />} />
          </Routes>
        </div>
      </Router>
    )
  }
}
