import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {

    constructor() {
        super()
        this.state = {
            articles: [],
            loading: false,
            page: 1,
        }
    }

    async componentDidMount() {
        let URL = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=988181bedd4b4afd92b8f02d3b7e536b&page=${this.state.page}`
        let data = await fetch(URL)
        let parseData = await data.json()
        console.log(parseData)
        this.setState({ articles: parseData.articles })
    }

    handleNextClick = async () => {
        let URL = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=988181bedd4b4afd92b8f02d3b7e536b&page=${this.state.page + 1}`
        let data = await fetch(URL)
        let parseData = await data.json()
        console.log(parseData)
        this.setState({
            articles: parseData.articles,
            page: this.page + 1
        })
    }

    handlePrevClick = async () => {
        let URL = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=988181bedd4b4afd92b8f02d3b7e536b&page=${this.state.page - 1}`
        let data = await fetch(URL)
        let parseData = await data.json()
        console.log(parseData)
        this.setState({
            articles: parseData.articles,
            page: this.page - 1
        })
    }

    render() {
        return (
            <div className='container my-3'>
                <h1>NewsPanda - Top Headlines</h1>
                <div className='row my-4'>
                    {this.state.articles.map((element) => {
                        return <div className='col-md-4 my-2 px-0' key={element.url}>
                            <NewsItem title={element.title.slice(0, 45)} description={element.description ? element.description.slice(0, 88) : " "} imgURL={element.urlToImage} newsURL={element.url} />
                        </div>
                    })}
                </div>
                <div className='container my-2 d-flex justify-content-between'>
                    <button type="button" className="btn btn-dark" onClick={handlePrevClick()} >&#x2B05; Previous </button>
                    <button type="button" className="btn btn-dark" onClick={handleNextClick()}>Next &#x27A1;</button>
                </div>
            </div>
        )
    }
}
